import "../styles/App.scss";
import ls from "../service/LocalStorage.js";
import dataApi from "../service/Api";
import { useState } from "react";
import {Route, Routes} from "react-router-dom";
import { FaTwitter } from 'react-icons/fa';

import Footer from "./Footer";
import Create from "./Create";
import Landing from "./Landing";


function App() {

  const [toggleCard, setToggleCard] = useState(false);
  const [toggleForm, setToggleForm] = useState("design");

  const [dataResult, setDataResult] = useState({});

  const [person, setPerson] = useState(
    ls.get("fullObject", {
      palette: "1",
      name: "",
      job: "",
      email: "",
      phone: "",
      github: "",
      linkedin: "",
      photo: "http://placekitten.com/200/300",
    })
  );

  const handleInput = (e) => {
    const inputValue = e.target.value;
    const inputName = e.target.name;
    setPerson({ ...person, [inputName]: inputValue });
    console.log(inputName);
    console.log(inputValue);
    ls.set("fullObject", person);

  };

  let selectedPalette = "palette1";
  if (person.palette === "2") {
    selectedPalette = "palette2";
  } else if (person.palette === "3") {
    selectedPalette = "palette3";
  } else {
    selectedPalette = "palette1";
  }

  const handleReset = () => {
    setPerson({
      palette: "1",
      name: "",
      job: "",
      email: "",
      phone: "",
      github: "",
      linkedin: "",
      photo: "",
    });
    ls.clear();
    setToggleCard(false);
  };
  const renderShareCode = () => {
    if (toggleCard === true) {
      return (
        <>
          <div className="created">

            {dataResult.success ? <><h4 className="created__title">La tarjeta ha sido creada:</h4> <a href={dataResult.cardURL} className="created__link" target="_blank" rel="noreferrer">Aquí tienes tu link: {dataResult.cardURL} </a></> : `Por cada campo vacío, un gatito llora`}
            <a href={`https://twitter.com/intent/tweet?text=Nueva%20tarjeta%20creada%20${dataResult.cardURL}`} className="twitter" target="_blank" rel="noreferrer">
            <FaTwitter></FaTwitter>
              Compartir en twitter
            </a>
          </div>
        </>
      );
    }
  }
    const handleClick = (e) => {
        e.preventDefault();

        setToggleCard(true);

        dataApi(person).then((data) => setDataResult(data));
        renderShareCode();
      };
  return (
    <>
      {/*<!------------------------LANDING PAGE------------------------------------>
      <Landing />

      {/*<!------------------------CREATE PAGE------------------------------------>*/}
      <Routes>
        <Route path="/" element={<Landing />}/>
        <Route path="/create" element={<Create handleReset={handleReset}
        selectedPalette={selectedPalette}
        person={person} handleInput={handleInput} dataResult={dataResult}  setToggleForm={setToggleForm} toggleForm={toggleForm}
        toggleCard={toggleCard}
        handleClick
        ={handleClick
        }/>}/>
        </Routes>
      <Footer/>
    </>
  );
}

export default App;
