import Design from "./Design";
import Fill from "./Fill";
import Share from "./Share";
import { useState } from "react";
const Form = (props)=> {
    const [toggleCard, setToggleCard] = useState(false);
    const handleClick = (e) => {
        e.preventDefault();
    
        setToggleCard(true);
    
        dataApi(person).then((data) => setDataResult(data));
      };
    const handleForm = (e) => {
        e.preventDefault();
      };
      const handleToggleForm = (value) => {
        props.setToggleForm(value);
      };
      //TODO:review
     const renderShareCode = () => {
        if (props.toggleCard === true) {
          return (
            <>
              <div className="created">
                
                {props.dataResult.success ? <><h4 className="created__title">La tarjeta ha sido creada:</h4> <a href={props.dataResult.cardURL} className="created__link" target="_blank" rel="noreferrer">Aquí tienes tu link: {props.dataResult.cardURL} </a></> : `Por cada campo vacío, un gatito llora`}
                <a href={`https://twitter.com/intent/tweet?text=Nueva%20tarjeta%20creada%20${props.dataResult.cardURL}`} className="twitter" target="_blank" rel="noreferrer">
                  {/* <FaTwitter></FaTwitter> */}
                  Compartir en twitter
                </a>
              </div>
            </>
          );
        }
      }
    return(
        <form method="get" action="" className="form" onSubmit={handleForm}>
          <Design
            handleToggleForm={handleToggleForm}
            person={props.person}
            toggleForm={props.toggleForm}
            handleInput={props.handleInput}
          />

          <Fill
            handleToggleForm={handleToggleForm}
            person={props.person}
            handleInput={props.handleInput}
            toggleForm={props.toggleForm}
          />

          <Share
            handleToggleForm={handleToggleForm}
            person={props.person}
            handleInput={props.handleInput}
            toggleForm={props.toggleForm}
            dataResult={props.dataResult}
            toggleCard={props.toggleCard}
            handleClick={props.handleClick}
            // todo:review
            renderShareCode={renderShareCode}
          />
        </form>

    )
}
export default Form;
